import { makeObservable, observable, action } from 'mobx';

class LocationDetailsStore {
    locationDetails = null;
    residents = [];

    constructor() {
        makeObservable(this, {
            locationDetails: observable,
            residents: observable,
            setLocationDetails: action,
            setResidents: action,
        });
    }

    fetchLocationDetails = (selectedLocation) => {
        try {
            const response =  fetch(
                `https://rickandmortyapi.com/api/location/${selectedLocation}`
            );
            const locationData =  response.json();
            this.setLocationDetails(locationData);
        } catch (error) {
            console.error(error);
        }
    };

    fetchResidents = () => {
        if (this.locationDetails && this.locationDetails.residents) {
            const residentsData = [];
            for (const residentUrl of this.locationDetails.residents) {
                try {
                    const response = fetch(residentUrl);
                    const resident = response.json();
                    residentsData.push(resident);
                } catch (error) {
                    console.error(error);
                }
            }
            this.setResidents(residentsData);
        }
    };

    setLocationDetails = (locationData) => {
        this.locationDetails = locationData;
    };

    setResidents = (residentsData) => {
        this.residents = residentsData;
    };
}

export default LocationDetailsStore;
