import { makeObservable, observable, action, computed } from 'mobx';

class CharacterStore {
    characters = [];

    constructor() {
        makeObservable(this, {
            characters: observable,
            fetchCharacters: action,
            filteredCharacters: computed,
            filterByName: action,
            filterBySpecies: action,
            filterByGender: action,
            filterByStatus: action,
            resetFilters: action,
        });
    }

    fetchCharacters() {
        fetch('https://rickandmortyapi.com/api/character')
            .then(response => response.json())
            .then(data => {
                this.characters = data.results;
            })
            .catch(error => {
                console.error(error);
            });
    }

    get filteredCharacters() {
        return this.characters.slice();
    }

    filterByName(name) {
        this.fetchCharacters(); // Reset the characters array to fetch all characters again
        const filteredCharacters = this.characters.filter(character =>
            character.name.toLowerCase().includes(name.toLowerCase())
        );
        this.characters.replace(filteredCharacters);
    }

    filterBySpecies(species) {
        this.fetchCharacters(); // Reset the characters array to fetch all characters again
        const filteredCharacters = this.characters.filter(character =>
            character.species.toLowerCase() === species.toLowerCase()
        );
        this.characters.replace(filteredCharacters);
    }

    filterByGender(gender) {
        this.fetchCharacters(); // Reset the characters array to fetch all characters again
        const filteredCharacters = this.characters.filter(character =>
            character.gender.toLowerCase() === gender.toLowerCase()
        );
        this.characters.replace(filteredCharacters);
    }

    filterByStatus(status) {
        this.fetchCharacters(); // Reset the characters array to fetch all characters again
        const filteredCharacters = this.characters.filter(character =>
            character.status.toLowerCase() === status.toLowerCase()
        );
        this.characters.replace(filteredCharacters);
    }

    resetFilters() {
        this.fetchCharacters();
    }
}

const store = new CharacterStore();

export default store;
