import React, { useEffect } from 'react';
import styles from './style.module.css';
import BtnGoBack from '../../components/btnGoBack';
import { Link } from 'react-router-dom';
import store from "../../store/store";
import {observer} from "mobx-react-lite";

const LocationDetails = observer(({ selectedLocation, handleCloseInfoLocation }) => {

    useEffect(() => {
        store.locationDetailsStore.fetchLocationDetails(selectedLocation).then(r=>r); // Fetch location details when the selectedLocation prop changes
        return () => store.locationDetailsStore.resetStore(); //
    }, [store.locationDetailsStore]);

    useEffect(() => {
        store.locationDetailsStore.fetchResidents().then(r=>r); // Fetch residents when location details are available
    }, [store.locationDetailsStore]);
    const { locationDetails, residents } = store.locationDetailsStore; // Get locationDetails and residents from the store

    return (
        <div className={styles.locationDetailsComp}>
            <div className={styles.btnPos}>
                <Link to='/locations'>
                    <BtnGoBack onClick={handleCloseInfoLocation}></BtnGoBack>
                </Link>
            </div>
            {locationDetails && (
                <div className={styles.main}>
                    <div className={styles.mainComp}>
                        <div className={styles.locationText}>{locationDetails.name}</div>
                        <div className={styles.compon}>
                            <div>
                                <h3>Type</h3>
                                <p>{locationDetails.type}</p>
                            </div>
                            <div>
                                <h3>Dimension</h3>
                                <p>{locationDetails.dimension}</p>
                            </div>
                        </div>
                    </div>
                    <div className={styles.resident}>
                        <h3>Resident</h3>
                        <div className={styles.residentPos}>
                            {residents.slice(0, 12).map((item, index) => (
                                <div key={index} className={styles.card}>
                                    <img src={item.image} alt='' />
                                    <div className={styles.name}>{item.name}</div>
                                    <div className={styles.species}>{item.species}</div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
});

export default LocationDetails;
